<script>
import { useGsap } from '#gsap'
export default {
  data: () => ({
    showContact: false,
    showNavbar: true,
    lastScrollPosition: 0,
    scrollOffset: 128,
    openDrawer: false,
  }),
  mounted() {
    this.lastScrollPosition = window.pageYOffset
    window.addEventListener('scroll', this.onScroll)
  },
  beforeDestroy() {
    window.removeEventListener('scroll', this.onScroll)
  },
  methods: {
    goToChauffage() {
      useGsap.to(window, { scrollTo: { y: '#chauffage', offsetY: 128 } })
    },
    goToPlomberie() {
      useGsap.to(window, { scrollTo: { y: '#plomberie', offsetY: 128 } })
    },
    goToSanitaire() {
      useGsap.to(window, { scrollTo: { y: '#sanitaire', offsetY: 128 } })
    },
    goToTop() {
      useGsap.to(window, { duration: 0, scrollTo: '0' })
    },
    drawer() {
      this.openDrawer = !this.openDrawer
    },
    contact() {
      this.showContact = !this.showContact
    },

    onScroll() {
      if (window.pageYOffset < 0) {
        return
      }
      if (
        Math.abs(window.pageYOffset - this.lastScrollPosition) <
        this.scrollOffset
      ) {
        return
      }
      this.showNavbar = window.pageYOffset < this.lastScrollPosition
      this.lastScrollPosition = window.pageYOffset
    },
  },
}
</script>

<style scoped>
.hidden-contact {
